import React, { Component } from 'react';
import { CardList } from './components/card-list/card-list.component';
import { SearchBox } from './components/search-box/search-box.component';
import './App.css';

class App extends Component{


  constructor(){

    super();
      this.state={
        meals: [],
        searchField: ''
      };
    }

  componentDidMount(){
    const nutrition = {
      "meal":[
        {
          "name":"Breakfast", 
          "description":"Egg Whites, Spinach, Avocado",
          "food":["egg white","boiled spinach","avocado"],
          "quantity":["8","303 g","187 g"],
          'calories':["128","70","300"]
        },
        {
          "name":"Lunch", 
          "description":"Salmon And Asparagus",
          "food":["grilled salmon","thyme","ground black pepper","boiled spinach"],
          "quantity":["165g","0.2tbsp","0.2tbsp","982g"],
          'calories':["302","0","0","226"]
        }

      ]
    }
  }

  handleChange = e => {
    this.setState({searchField: e.target.value})
  }


  render(){
    const { meals, searchField } = this.state;
    const filteredMeals = meals.filter(meal =>
      meal.strCategory.toLowerCase().includes(searchField.toLowerCase())
    )
    return (
      <div className="App">
        <h1>Meal Planner</h1>
        <SearchBox placeholder='search meals' 
          handleChange={this.handleChange}/>
      </div>
    );
    }
}

export default App;
