import React, { Component } from 'react';
import { CardList } from './components/card-list/card-list.component';
import { SearchBox } from './components/search-box/search-box.component';
import './App.css';

class App extends Component{


  constructor(){
    super();
   this.state={
    meals: [
      {
        name: "Breakfast",
        description: "Oatmeal",
        calories: "250",
        health_rating: "average 9.2",
        imgurl: "https://images.pexels.com/photos/90894/pexels-photo-90894.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260", 
        id: 'ascr1'
      },
      {
        name: "Lunch",
        description: "Cheeseburger and Fries",
        calories: "750", 
        health_rating: "below average 6",
        imgurl: "https://images.pexels.com/photos/551991/pexels-photo-551991.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260", 
        id: 'ascr2'
      }
    ],
    searchField: ''
   };
  }
  handleChange = e => {
    this.setState({searchField: e.target.value})
  }

  render(){
    const { meals, searchField } = this.state;
    const filteredMeals = meals.filter(meal =>
        meal.name.toLowerCase().includes(searchField.toLowerCase())
      )
    return (
      <div className="App">
        <h1>Meal Planner</h1>
        <SearchBox placeholder='search meals' 
          handleChange={this.handleChange}/>
        <CardList monsters = {filteredMeals}>
        </CardList>
      </div>
    );
    }
}

export default App;
